                    lat: point.base.lat,
                    lon: point.base.lon,
                    name: track_name+"_annotation_"+i,
                    icon: "fa-xl fa-sticky-note-o",
                    iconColor: "cyan"
                }

            if(point.annotation === "recording_start"){
                point_icon.icon = "fa-xl fa-play"
                point_icon.label = null
            } 
            if(point.annotation === "recording_stop"){
                point_icon.icon = "fa-xl fa-pause"
